import { useCallback } from "react";
import Particles from "react-particles";
import { loadFull } from "tsparticles";

const SnowParticles = () => {
  const particlesInit = useCallback(async engine => {
    console.log(engine);
    // you can initiate the tsParticles instance (engine) here, adding custom shapes or presets
    // this loads the tsparticles package bundle, it's the easiest method for getting everything ready
    // starting from v2 you can add only the features you need reducing the bundle size
    await loadFull(engine);
  }, []);

  const particlesLoaded = useCallback(async container => {
    await console.log(container);
  }, []);

  return (
    <Particles
      id="tsparticles"
      init={particlesInit}
      loaded={particlesLoaded}
      options={{
        detectRetina: true,
        fpsLimit: 144,
        interactivity: {
          detectsOn: "canvas",
          events: {
            onClick: {
              enable: false,
              mode: "repulse"
            },
            onDiv: {
              elementId: "repulse-div",
              enable: false,
              mode: "repulse"
            },
            onHover: {
              enable: false,
              mode: "repulse",
              parallax: {
                enable: false,
                force: 60,
                smooth: 10
              }
            },
            resize: true
          },
          modes: {
            bubble: {
              distance: 400,
              duration: 2,
              opacity: 1,
              size: 40,
              speed: 3
            },
            connect: {
              distance: 80,
              lineLinked: {
                opacity: 0.5
              },
              radius: 60
            },
            grab: {
              distance: 400,
              lineLinked: {
                opacity: 1
              }
            },
            push: {
              quantity: 1
            },
            remove: {
              quantity: 2
            },
            repulse: {
              distance: 200,
              duration: 0.4
            }
          }
        },
        particles: {
          color: {
            value: "#ffffff"
          },
          lineLinked: {
            blink: false,
            color: "#000",
            consent: false,
            distance: 150,
            enable: false,
            opacity: 0,
            width: 0
          },
          rotate: {
            value: 90,
            random: false,
            direction: "clockwise",
            animation: {
              enable: false,
              speed: 5,
              sync: false
            }
          },
          move: {
            attract: {
              enable: false,
              rotateX: 600,
              rotateY: 1200
            },
            bounce: false,
            direction: "bottom",
            enable: true,
            outMode: "out",
            random: true,
            speed: 30,
            straight: true
          },
          number: {
            density: {
              enable: true,
              area: 800
            },
            limit: 0,
            value: 200
          },
          opacity: {
            animation: {
              enable: false,
              minimumValue: 0.1,
              speed: 1,
              sync: false
            },
            random: false,
            value: 0.5
          },
          shape: {
            character: {
              fill: false,
              font: "Verdana",
              style: "",
              value: "*",
              weight: "400"
            },
            image: [],
            polygon: {
              nb_sides: 5
            },
            stroke: {
              color: "#efefefee",
              width: 1
            },
            type: "line"
          },
          size: {
            animation: {
              enable: false,
              minimumValue: 0.1,
              speed: 40,
              sync: false
            },
            random: false,
            value: 10
          }
        },
        polygon: {
          draw: {
            enable: false,
            lineColor: "#ffffff",
            lineWidth: 0.5
          },
          move: {
            radius: 10
          },
          scale: 1,
          type: "none",
          url: ""
        },
      }}
    />
  );
};

export default SnowParticles;
