import React from "react";
import Particles from "react-tsparticles";

function ParticleComponent() {
  return (
    <Particles
      id="tsparticles"
      options={{
        background: {
          color: {
            value: "#ffffff",
          },
        },
        particles: {
          color: {
            value: "#000000",
          },
          shape: {
            type: "circle",
          },
          size: {
            value: 3,
            random: true,
            anim: {
              enable: false,
            },
          },
          number: {
            value: 150,
            density: {
              enable: true,
              value_area: 800,
            },
          },
        },
        interactivity: {
          events: {
            onhover: {
              enable: true,
              mode: "repulse",
            },
          },
        },
      }}
    />
  );
}

export default ParticleComponent;
